_source.option_default:
lib.mapAttrs (key: val:
  (pkgs.formats.elixirConf { }).lib.mkMap { name = val.name; ref = val.ref; })
  config.services.akkoma.frontends;

if lib.versionOlder config.system.stateVersion "24.05"
then "$\{httpConf.scheme}://$\{httpConf.host}:$\{builtins.toString httpConf.port}"
else null;

if lib.versionOlder config.system.stateVersion "24.05"
then "$\{httpConf.scheme}://$\{httpConf.host}:$\{builtins.toString httpConf.port}/media/"
else null;

_source.option_example:
{
  service_addr = ":1323";
  domain = "autoconfig.example.org";
  imap = {
    server = "example.org";
    port = 993;
  };
  smtp = {
    server = "example.org";
    port = 465;
  };
}

[
  {
    addr = "195.154.1.1";
    port = 443;
    ssl = true;
  }
  {
    addr = "192.154.1.1";
    port = 80;
  }
  {
    addr = "unix:/var/run/nginx.sock";
  }
]

{
  my_contacts = {
    a = "my_cloud_contacts";
    b = "my_local_contacts";
    collections = [ "from a" ];
    conflict_resolution = "a wins";
    metadata = [ "color" "displayname" ];
  };
};

{
  my_cloud_contacts = {
    type = "carddav";
    url = "https://dav.example.com/";
    read_only = true;
    username = "user";
    "password.fetch" = [ "command" "cat" "/etc/vdirsyncer/cloud.passwd" ];
  };
  my_local_contacts = {
    type = "carddav";
    url = "https://localhost/";
    username = "user";
    "password.fetch" = [ "command" "cat" "/etc/vdirsyncer/local.passwd" ];
  };
}
